package Looping;

public class FarmerProblem {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=3;i++) {
			for(int j=1;j<=5;j++) {
				System.out.println("Planting seed in row "+i+" column "+j );
			}
		}
	System.out.println("The planatation of seed is completed ");
	}

}
